	if (fextension.charAt(j) == ',') {
		exts[i] = buff.toString();
		buff = new StringBuffer();
		i++;
	} else {
		buff.append(fextension.charAt(j));
	}
}
exts[i] = buff.toString();
properties.extensions=exts;
} else {
	properties.extensions=null;
}

